mapper.update:
UPDATE 
   mini_market_chatroom 
SET 
   chatroom_active = #{chatroomActive}
WHERE 
   chatroom_no = #{chatroomNo}

mapper.select:
SELECT 
	COUNT(*) 
FROM 
	mini_market_chatroom
WHERE 
	member_item_no = #{memberItemNo}
AND
	member_unique_no = #{memberUniqueNo}
AND
	member_id = #{memberId}
